import sys, json, datetime
data = json.load\(sys.stdin\)
for job in data.get\(''jobs'', []\):
    if ''API Image'' in job[''name'']:
        print\(f""Job: {job[''name'']}""\)
        print\(f""Status: {job.get\(''status''\)}""\)
        print\(f""\\nSteps:""\)
        for step in job.get\(''steps'', []\):
            if step.get\(''started_at''\):
                start = datetime.datetime.fromisoformat\(step[''started_at''].replace\(''Z'', ''+00:00''\)\)
                if step.get\(''completed_at''\):
                    end = datetime.datetime.fromisoformat\(step[''completed_at''].replace\(''Z'', ''+00:00''\)\)
                    elapsed = int\(\(end - start\).total_seconds\(\)\)
                else:
                    now = datetime.datetime.now\(datetime.timezone.utc\)
                    elapsed = int\(\(now - start\).total_seconds\(\)\)

                status = step.get\(''conclusion''\) or step.get\(''status''\)
                mins = elapsed // 60
                secs = elapsed % 60
                print\(f""  {step[''name'']}: {status} \({mins}m {secs}s\)""\)
")

